    /**
     * Overrides the corresponding function in fcn_gate_library. Given a tile `t`, this function takes all necessary
     * information from the stored grid into account to choose the correct fcn_gate representation for that tile.
     * Rotation and special marks like input and output, const cells etc. are computed additionally.
     *
     * @tparam GateLyt Pointy-top hexagonal gate-level layout type.
     * @param lyt Layout that hosts tile `t`.
     * @param t Tile to be realized as a Bestagon wire.
     * @return Bestagon wire representation of `t` including mirroring.
     */
    template <typename GateLyt>
    [[nodiscard]] static fcn_gate set_up_gate(const GateLyt& lyt, const tile<GateLyt>& t)
    {
        static_assert(is_gate_level_layout_v<GateLyt>, "GateLyt must be a gate-level layout");
        static_assert(is_hexagonal_layout_v<GateLyt>, "GateLyt must be a hexagonal layout");
        static_assert(has_pointy_top_hex_orientation_v<GateLyt>, "GateLyt must be a pointy-top hexagonal layout"); //FRAGE: In theory I souldn't need this, but I think I do due to current implementations?

        const auto n = lyt.get_node(t);
        const auto p = determine_port_routing(lyt, t);

        //FRAGE: wof체r stehen das "has" und "v" in "has_is_fanout_v" etcc.
        try
        {
            if constexpr (fiction::has_is_buf_v<GateLyt>) // buf = buffer = wire
            {
                if (lyt.is_buf(n))
                {
                    if (lyt.is_ground_layer(t))
                    {
                        // two wires in the same tile -> double wire
                        if (const auto at = lyt.above(t); (t != at) && lyt.is_wire_tile(at))
                        {
                            const auto pa = determine_port_routing(lyt, at);

                            return DOUBLE_WIRE_MAP.at({p, pa});
                        }

                        //FORME: first set the ports with determine_port_routing, then look the right wire up with these in the wire map,
                        // definition of the wire is given in the constexpr in the private section
                        
                        // single wire
                        return SINGLE_WIRE_MAP.at(p);
                    }

                    return EMPTY_GATE;
                }
            }
        }
        catch(const std::out_of_range&)
        {
            throw unsupported_gate_orientation_exception(t, p);
        }

        throw unsupported_gate_type_exception(t);
    }

